// Function to find the maximum element in a vector using a lambda
int findMax(const std::vector<int>& vec) {
    if (vec.empty()) {
        return -1; // Or throw an exception
    }
    return *std::max_element(vec.begin(), vec.end());
}

// Function to filter even numbers using a lambda
std::vector<int> filterEven(const std::vector<int>& vec) {
    std::vector<int> evens;
    for (int num : vec) {
        if (num % 2 == 0) {
            evens.push_back(num);
        }
    }
    return evens;
}

int main() {
    std::vector<int> data = {10, 25, 3, 40, 15, 8};

    int max_val = findMax(data);
    std::cout << "Max value: " << max_val << std::endl;

    std::vector<int> even_numbers = filterEven(data);
    std::cout << "Even numbers: ";
    for (int num : even_numbers) {
        std::cout << num << " ";
    }
    std::cout << std::endl;

    // Using a lambda directly for sorting (more advanced)
    std::sort(data.begin(), data.end(), [](int a, int b) { 
        return a > b; 
    });
    std::cout << "Sorted (descending): ";
    for (int num : data) {
        std::cout << num << " ";
    }
    std::cout << std::endl;

    return 0;
}

