#include <iostream>

// Function to swap two integers using pointers
void swapPointers(int* a, int* b) {
    int temp = *a;
    *a = *b;
    *b = temp;
}

// Function to swap two integers using references
void swapReferences(int& a, int& b) {
    int temp = a;
    a = b;
    b = temp;
}

int main() {
    int x = 10, y = 20;
    std::cout << "Before swap (pointers): x = " << x << ", y = " << y << std::endl;
    swapPointers(&x, &y);
    std::cout << "After swap (pointers): x = " << x << ", y = " << y << std::endl;

    int p = 5, q = 7;
    std::cout << "Before swap (references): p = " << p << ", q = " << q << std::endl;
    swapReferences(p, q);
    std::cout << "After swap (references): p = " << p << ", q = " << q << std::endl;

    int value = 42;
    int* ptr = &value; // Pointer to value
    int& ref = value;  // Reference to value

    std::cout << "Value: " << value << ", Pointer value: " << *ptr << ", Reference value: " << ref << std::endl;

    return 0;
}

