// Template class for a simple pair
template <typename T1, typename T2>
class Pair {
public:
    T1 first;
    T2 second;

    Pair(T1 f, T2 s) : first(f), second(s) {}

    void display() {
        std::cout << "Pair: (" << first << ", " << second << ")" << std::endl;
    }
};

int main() {
    // Using template function with different types
    std::cout << "Int sum: " << add(5, 7) << std::endl;
    std::cout << "Double sum: " << add(3.5, 2.1) << std::endl;
    std::cout << "Char sum (ASCII): " << add('A', 1) << std::endl;

    // Using template class with different types
    Pair<int, double> p1(10, 20.5);
    p1.display();

    Pair<std::string, bool> p2("Active", true);
    p2.display();

    return 0;
}
