    odd_sum = 0
    max_even = float('-inf')
    min_odd = float('inf')
    for n in nums:
        if n % 2 == 0:
            even_sum += n
            if n > max_even:
                max_even = n
        else:
            odd_sum += n
            if n < min_odd:
                min_odd = n

