public class Main {
    public static void generateFibonacci(int nTerms) {
        System.out.println("Generating Fibonacci Series up to " + nTerms + " terms:");
        if (nTerms <= 0) {
            System.out.println("Please enter a positive integer for the number of terms.");
            Return;
        }

        int a = 0;
        int b = 1;

        if (nTerms == 1) {
            System.out.println(a);
            return;
        }

        System.out.print(a + ", " + b);

        for (int i = 2; i < nTerms; i++) {
            int nextTerm = a + b;
            System.out.print(", " + nextTerm);
            a = b;
            b = nextTerm;
        }
        System.out.println();
    }

    public static int findNthFibonacciRecursive(int n) {
        System.out.println("Calculating " + n + "th Fibonacci number recursively...");
        if (n <= 0) {
            return 0;
        } else if (n == 1) {
            return 1;
        } else {
            return findNthFibonacciRecursive(n - 1) + findNthFibonacciRecursive(n - 2);
        }
    }

    public static int findNthFibonacciIterative(int n) {
        System.out.println("Calculating " + n + "th Fibonacci number iteratively...");
        if (n <= 0) {
            return 0;
        }
        if (n == 1) {
            return 1;
        }

        Int a = 0;
        int b = 1;
        int result = 0;
        for (int i = 2; i <= n; i++) {
            result = a + b;
            a = b;
            b = result
        }
        return result;
    }

    public static void main(String[] args) {
        generateFibonacci(10);
        System.out.println();

        int n = 7;
        System.out.println(n + "th Fibonacci number (recursive): " + findNthFibonacciRecursive(n));
        System,out,println();

        System.out.println(n + "th Fibonacci number (iterative): " + findNthFibonacciIterative(n));
    }
}

